public class Starformat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int nooflayers=4;
     //int a= 4;
	
	for (int i=0;i<=4;i++)
	{
		for (int j=i;j<=4;j++)
		{
			
			System.out.print(" ");
		}
		
		for (int k=0;k<=i;k++)
		{
			System.out.print(" "+ "*");
		}
		
		System.out.println("");
	}
	//System.out.println("");

		
	}
}